mapper.select:
SELECT api_id, api_name, api_type, api_status, group_id FROM base_api_info where api_status in ('edit','release')
and group_id = #{groupId}
and create_by = #{userId}

mapper.update:
UPDATE base_api_info
enabled = #{enabled},api_status = #{apiStatus}, api_name= #{apiName}, api_path = #{apiPath}, api_type = #{apiType}, api_method = #{apiMethod}, auth_type = #{authType}, group_id = #{groupId}, release_time = #{releaseTime}, version = #{version}, parent_id = #{parentId}, tenant_id = #{tenantId}, remarks = #{remarks}, sql_type = #{sqlType}, result_type = #{resultType}, sql_script = #{sqlScript}, show_sql_script =#{showSqlScript}, datasource_id =#{datasourceId}, datasource_type =#{datasourceType}, schema_name =#{schemaName}, page_setup =#{pageSetup}, update_by = #{updateBy}, update_time = #{updateTime}
WHERE api_id =#{apiId}

UPDATE base_api_info
enabled = #{enabled}, api_status = #{apiStatus}, release_time = #{releaseTime}, update_by = #{updateBy}, update_time = #{updateTime},
WHERE api_id =#{apiId}
